INSERT INTO `teammates` (`id`, `user_id`, `employee`, `description`, `description_max`, `image`, `gram_image`, `created_at`, `updated_at`) VALUES
(1, 11, 'Лукаш Антон Иванович', 'Заместитель директора общества с ограниченной ответственностью \"Волгоградский центр судебных экспертиз\", член Исполнительного комитета Общероссийского общественного движения \"Корпус \"За Чистые Выборы\"', '<p style=\"text-align:justify\">Член Исполкома ООД &laquo;Корпус &laquo;За Чистые выборы&raquo;, координатор и эксперт Ассоциации &laquo;Независимый общественный мониторинг&raquo; (направления &ndash; работа с медиа средой, информационная безопасность, антифейк).<br />\r\nЭксперт Агентства Стратегических Инициатив (направления &ndash; стратегическое социально-экономическое развитие региона, эффективность бизнеса, психология управления, управление проектами).<br />\r\nЧлен Общественной палаты Волгоградской области (направления &ndash; право, общественный контроль, работа со СМИ).<br />\r\nЧлен Экспертного совета при Уполномоченном по правам предпринимателей Волгоградской области (направление &ndash; правовое сопровождение бизнеса).<br />\r\nЧлен Общественного Совета при Комитете по образованию и науки Администрации Волгоградской области (направления &ndash; профилактика девиантного поведения молодежи, информационная и ценностная безопасность, защита исторической памяти).<br />\r\nАвтор проекта по вопросам правового просвещения и информационной безопасности, ведущий еженедельной программы &laquo;Правовой разворот&raquo; на Волгоградском муниципальном телевидении.<br />\r\nЭксперт на радио &laquo;Комсомольская правда-Волгоград&raquo; и на телевидении (передачи &laquo;Пусть говорят&raquo;, &laquo;Общественная экспертиза&raquo;, &laquo;Итоги дня&raquo;, &laquo;Резонанс&raquo;).</p>', '[\"a8b42b34a7246d626833400c91b282f9.jpg\"]', '', NULL, '2022-05-23 08:56:12'),
(2, 11, 'Гайдадина Ирина Вадимовна', 'Почетный работник среднего профессионального образования Волгоградской области ,Заведующий кафедрой юридических дисциплин АНПОО &quot;Академический колледж&quot;', '', '[\"b48fbc071bb84cdf7aa87d04eec12662.jpg\"]', '', NULL, '2022-05-02 18:19:27'),
(3, 11, 'Крылова Виктория Александровна', 'Эксперт Независимого общественного мониторинга, преподаватель высшей квалификационной категории ГБПОУ \"Волгоградский технический колледж\", почетный работник среднего профессионального образования Волгоградской области.', '<p>1979 года рождения. В 2001 г. окончила Волгоградский Государственный педагогический университет, специальность &laquo;физика&raquo; с дополнительной специальностью &laquo;информатика&raquo;, квалификация учитель физики. Крылова В.А. в настоящее время является преподавателем ГБПОУ &laquo;Волгоградский технический колледж&raquo; ведет научную, организационную и методическую работу, участвует в международных и российских научных конференциях, и симпозиумах, руководит научной работой студентов. Имеет звание почетного работника среднего профессионального образования Волгоградской области. Активно занимается общественной деятельностью и является членом Независимого общественного мониторинга и Общероссийского общественного движения Корпус &laquo;За чистые выборы&raquo;.</p>', '[\"9f7e1c73220bf71d8c9de1f253f1abe6.jpg\",\"8204ecfcd6cabb816eed39c07bed3ab7.jpg\",\"72808aaa0d0dab78976243f27127799c.jpg\",\"9b0fa3f3d57b903c2ddd800e1747bd7f.jpg\",\"8b03d041d46fababff1cb90f51c7d9e3.jpg\"]', '', NULL, '2022-05-04 20:30:13'),
